function toAzmu2dClusters(centers, clusters)
{
  var ret = [];
  for (var i=0; i< clusters.length; i++)
  {
    var azm2d = azimuthal2(centers[i], clusters[i]);
    ret.push(azm2d);
  }
  return ret;

}


function toAzmu2dClustersNormalize(centers, clusters)
{
  var ret = [];
  for (var i=0; i< clusters.length; i++)
  {
     var pts = normalizeArray(clusters[i]);
    var azm2d = azimuthal2(centers[i], pts);
    ret.push(azm2d);
  }
  return ret;

}

function normalizeArray( pts)
{
  var ret =[];
  for (var i=0; i< pts.length; i++)
  {
    var p = pts[i].clone().normalize();
    ret.push(p);
  }
  return ret;
}

function checkSplitNeeded (clusters)
{
  var flags =[];
  var areas =[];
  var totalArea =0.0;

  var cutoff = 1.0/16.0;

  for (var i=0; i<clusters.length; i++)
  {
     s= Math.abs(PolyArea(clusters[i][0]));
     areas.push(s);
     totalArea += s;
     flags.push(false);
  }

  for (var i=0; i< areas.length; i++)
  {
    // if the cluster has hole, the polygon array will have a length of more than 1
    // in which case we don't split the polygon, because we don't know 
    // how to split polygon with hole(s) yet.
    if ((clusters[i].length ==1 ) && (areas[i]/totalArea > cutoff))
    {
      flags[i] = true;
    }
  }

  return flags;

}



//  remove all stray edges
// yet keeps holes.
// returned array format:  [[cluster 1], [cluster 2]...]
// each cluster has the format of:
//[[outerPolygon], [hole 1], [hole 2], ...]  with holes as optional.
//
function clean2dClusters( cluster2d)
{
  var cleanClusters =[];
  for (var x=0; x < cluster2d.length; x++)
  {
    var v2array =[];
    for (var i=0; i< cluster2d[x].length; i++)
    {
      var v2 = new  THREE.Vector2(cluster2d[x][i][0], cluster2d[x][i][1]);
      v2array.push(v2);

    }

    var heartShape = new THREE.Shape(v2array);
    var geometry2 = new THREE.ShapeGeometry( heartShape );
    var material2 = new THREE.MeshBasicMaterial( { color: 0x00ff00 } );
    var mesh2 = new THREE.Mesh( geometry2, material2 ) ;

    var edges = new THREE.EdgesGeometry( geometry2 );

    var positions = edges.getAttribute("position");

    var polyTest = stitchPoly(positions.array);  //cleanPoly
    if ( polyTest.length == 1)
    {
      if (PolyArea(polyTest[0]) < 0)
      {
        polyTest[0] = reverseOrder(polyTest[0]);
      }
      cleanClusters.push(polyTest);
    }
    else
    {
      var handledPolys = handleHoles(polyTest);
      cleanClusters.push(handledPolys);
    }
  }

  return cleanClusters;
}

function reverseOrder (data)
{
  var ret = [];
  for (var i= data.length -1; i >=0; i--)
  {
    ret.push(data[i]);
  }
  return ret;
}

function handleHoles (polys)
{
  // calculate the area of each polygon, the one with biggest area
  // is the outer polygon. throw out tiny holes, which are simply artifacts
  // of the messy original cluster data
  var areas = []
  var maxAreaIndex =0
  var maxArea = 0
  for (var i=0; i< polys.length; i++)
  {
    s = PolyArea(polys[i]);
    if (s < 0)
    {
      polys[i] = reverseOrder(polys[i]);
    }
    s = Math.abs(s); // make sure it is positive
    areas.push(s);
    if (s > maxArea)
    {
      maxArea = s;
      maxAreaIndex = i;
    }

  }

  var ret = [];
  // the biggest polygon first
  ret.push(polys[maxAreaIndex]);
  for (var i=0; i< polys.length; i++)
  {
    if (i != maxAreaIndex)
    {
      if (areas[i]/maxArea > 0.05) // 5 percent
      {
        ret.push(polys[i]);
      }
    }
  }
  return ret;

}


// postions are the buffered array [x,y,z, x, y,z...] and Z always 0
function stitchPoly (positions)
{
  var points =[];
  for (var i=0; i< positions.length-2; i+=3)
  {
    points.push([positions[i], positions[i+1]]);
  }

  var parent =[];
  var first =[];
  first.push(points[0]); first.push(points[1]);
  parent.push(first);
  for (var i=2; i< points.length-1; i+=2)
  {
    //debug
    //var rep = findRepeat(parent);
    
    //if (rep[0] >=0)
   // {
    //  console.log(rep);
    //}
    ///////////

     var p1= points[i];
     var p2 = points[i+1];

     var connected = false;
     var linked = false;  // linked to another cluster
     for (var j=0; j< parent.length; j++)
     {
       var cluster = parent[j];
       var l = cluster.length -1;
       if (same2d(p1, cluster[0]))
       { 
         connected = true;
         var info = getConnectIndex2(p2, parent, j+1 );
         if (info[0] >= 0)
         {
           linked = true;
           var k = info[0]; 
           var matchArray = parent[k];
           if (info[1] == true)
           {
             // matching head
             // reversely add parent[k] into the head of cluster.
             for (var m= 0; m < matchArray.length; m++ )
             {
               cluster.unshift(matchArray[m]);
             }
             parent[j] = cluster;
           }
           else
           {
             // matchin tail:
             cluster = matchArray.concat(cluster);
             parent[j] = cluster;
           }
           // remove matchArray from parent
           parent.splice(k,1);
         }
         else
         {
           // not linked, simply put p2 at the head
           cluster.unshift(p2);
           parent[j] = cluster;
           
         }
         break;
        }
       else if (same2d (p1, cluster[l]))
       {
         connected = true;
         var info = getConnectIndex2(p2, parent, j+1);
         if (info[0] >= 0)
         {
           linked = true;
           var k = info[0];
           var matchArray = parent[k];
           if (info[1] == true)
           {
             // match head
             cluster = cluster.concat(matchArray);
             parent[j] = cluster;
           }
           else
           {
             // match tail
             for (var m = matchArray.length -1; m >=0; m--)
             {
               cluster.push(matchArray[m]);
             } 
             parent[j] = cluster;
           }
            // remove matchArray from parent
            parent.splice(k,1);
         }
         else
         {
           // not linked, simply put p2 at the end
           cluster.push(p2); 
           parent[j] = cluster;
         }
         break;
       }    ///
       else if (same2d(p2, cluster[0]))
       { 
         connected = true;
         var info = getConnectIndex2(p1, parent, j+1 );
         if (info[0] >= 0)
         {
           linked = true;
           var k = info[0]; 
           var matchArray = parent[k];
           if (info[1] == true)
           {
             // matching head
             // reversely add parent[k] into the head of cluster.
             for (var m= 0; m < matchArray.length; m++ )
             {
               cluster.unshift(matchArray[m]);
             }
             parent[j] = cluster;
           }
           else
           {
             // matchin tail:
             cluster = matchArray.concat(cluster);
             parent[j] = cluster;
           }
           // remove matchArray from parent
           parent.splice(k,1);
         }
         else
         {
           // not linked, simply put p1 at the head
           cluster.unshift(p1);
           parent[j] = cluster;
           
         }
         break;
       }
       else if (same2d (p2, cluster[l]))
       {
         connected = true;
         var info = getConnectIndex2(p1, parent, j+1);
         if (info[0] >= 0)
         {
           linked = true;
           var k = info[0];
           var matchArray = parent[k];
           if (info[1] == true)
           {
             // match head
             cluster =cluster.concat(matchArray);
             parent[j] = cluster;
           }
           else
           {
             // match tail
             for (var m = matchArray.length -1; m >=0; m--)
             {
               cluster.push(matchArray[m]);
             } 
             parent[j] = cluster;
           }
            // remove matchArray from parent
            parent.splice(k,1);
         }
         else
         {
           // not linked, simply put p1 at the end
           cluster.push(p1); 
           parent[j] = cluster;
         }
         break;
       }

     }  // inner for loop for parent

     if (connected == false)
     {
       parent.push([p1,p2]);
     }


  } // outer for loop

  return parent;

}

function getConnectIndex (p, parent, start)
{
  if (start < parent.length)
  {
    for (var i= start; i< parent.length; i++)
    {
      var points = parent[i];
      var l = points.length-1;
      if (same(p, points[0]))
      {
        return [i, true];  // true: match head
      }
      else if (same(p, points[l]))
      {
        return [i, false] // match tail
      }
      
    }
  }

  // check from begining to start-2
  if (start > 1)
  {
    for (var i= 0; i< start-1; i++)
    {
      var points = parent[i];
      var l = points.length-1;
      if (same(p, points[0]))
      {
        return [i, true];  // true: match head
      }
      else if (same(p, points[l]))
      {
        return [i, false] // match tail
      }
    
    }
  }

  return [-1, false];

}


