function same( p1, p2)
{
  //return p1.equals(p2);

  if ( p1.manhattanDistanceTo(p2) < 0.01)
  {
    return true;
  }
  return false;
}

function same2d( p1, p2)
{
  var dis = Math.abs(p1[0] - p2[0]) + Math.abs(p1[1]-p2[1]);
  if (dis < 0.001)
  {
    return true;
  }
  return false;
}



